INSERT INTO `artical_data_copy2` VALUES ('J', 'BECKER, F   CHOUDHURY, BJ', 'BECKER, F   CHOUDHURY, BJ', 'RELATIVE SENSITIVITY OF NORMALIZED DIFFERENCE VEGETATION INDEX (NDVI)   AND MICROWAVE POLARIZATION DIFFERENCE INDEX (MPDI) FOR VEGETATION AND   DESERTIFICATION MONITORING', 'REMOTE SENSING OF ENVIRONMENT', 'English', 'NoteRP BECKER, F (通讯作者)，NASA,GODDARD SPACE FLIGHT CTR,HYDROL SCI BRANCH,GREENBELT,MD 20771, USA.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 21, 160, 171, 6, 125, 'ELSEVIER SCIENCE INC', 'NEW YORK', '655 AVENUE OF THE AMERICAS, NEW YORK, NY 10010', '0034-4257', NULL, 'REMOTE SENS ENVIRON', 'Remote Sens. Environ.', 'MAR', 1988, '24IS 2BP 297EP 311', NULL, '10.1016/0034-4257(88)90031-4', 15, 'Environmental Sciences; Remote Sensing; Imaging Science & Photographic   Technology', 'Science Citation Index Expanded (SCI-EXPANDED)', 'Environmental Sciences & Ecology; Remote Sensing; Imaging Science &   Photographic Technology', 'M5501', 'WOS:A1988M550100006', NULL, '2023-11-05', NULL);
