INSERT INTO `artical_data_copy5` VALUES ('J', 'CHUAH, HT   TAN, HS', 'CHUAH, HT   TAN, HS', 'A MULTICONSTITUENT AND MULTILAYER MICROWAVE BACKSCATTER MODEL FOR A   VEGETATIVE MEDIUM', 'REMOTE SENSING OF ENVIRONMENT', 'English', 'ArticleRP CHUAH, HT (通讯作者)，UNIV MALAYA,DEPT ELECT ENGN,KUALA LUMPUR 59100,MALAYSIA.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 16, 6, 6, 0, 1, 'ELSEVIER SCIENCE INC', 'NEW YORK', '655 AVENUE OF THE AMERICAS, NEW YORK, NY 10010', '0034-4257', NULL, 'REMOTE SENS ENVIRON', 'Remote Sens. Environ.', 'FEB', 1990, '31IS 2BP 137EP 153', NULL, '10.1016/0034-4257(90)90062-Q', 17, 'Environmental Sciences; Remote Sensing; Imaging Science & Photographic   Technology', 'Science Citation Index Expanded (SCI-EXPANDED)', 'Environmental Sciences & Ecology; Remote Sensing; Imaging Science &   Photographic Technology', 'DQ428', 'WOS:A1990DQ42800005', NULL, '2023-11-05', NULL);
