INSERT INTO `artical_data_copy5` VALUES ('J', 'GUENTHER, B   MCLEAN, J   LEROY, M   HENRY, P', 'GUENTHER, B   MCLEAN, J   LEROY, M   HENRY, P', 'COMPARISON OF CNES SPHERICAL AND NASA HEMISPHERICAL LARGE APERTURE   INTEGRATING SOURCES .1. USING A LABORATORY TRANSFER SPECTRORADIOMETER', 'REMOTE SENSING OF ENVIRONMENT', 'English', 'Article', NULL, NULL, NULL, 'CTR NATL ETUD SPATIALES,TOULOUSE,FRANCE.RP GUENTHER, B (通讯作者)，NASA,GODDARD SPACE FLIGHT CTR,SPACE & EARTH SCI DIRECTORATE,CODE 673,GREENBELT,MD 20771, USA.', NULL, NULL, NULL, NULL, NULL, 3, 8, 8, 0, 0, 'ELSEVIER SCIENCE INC', 'NEW YORK', '655 AVENUE OF THE AMERICAS, NEW YORK, NY 10010', '0034-4257', NULL, 'REMOTE SENS ENVIRON', 'Remote Sens. Environ.', 'FEB', 1990, '31IS 2BP 85EP 95', NULL, '10.1016/0034-4257(90)90058-T', 11, 'Environmental Sciences; Remote Sensing; Imaging Science & Photographic   Technology', 'Science Citation Index Expanded (SCI-EXPANDED)', 'Environmental Sciences & Ecology; Remote Sensing; Imaging Science &   Photographic Technology', 'DQ428', 'WOS:A1990DQ42800001', NULL, '2023-11-05', NULL);
