INSERT INTO `artical_data_copy5` VALUES ('J', 'HARDING, DJ   WIRTH, KR   BIRD, JM', 'HARDING, DJ   WIRTH, KR   BIRD, JM', 'SPECTRAL MAPPING OF ALASKAN OPHIOLITES USING LANDSAT THEMATIC MAPPER   DATA', 'REMOTE SENSING OF ENVIRONMENT', 'English', 'Article', NULL, NULL, NULL, 'CORNELL UNIV,DEPT GEOL SCI,ITHACA,NY 14853.', 'Cornell UniversityRP HARDING, DJ (通讯作者)，NASA,GODDARD SPACE FLIGHT CTR,TERR PHYS LAB,CODE 622,GREENBELT,MD 20771, USA.RI Harding, David/F-5913-2012', NULL, NULL, NULL, NULL, 12, 14, 14, 0, 4, 'ELSEVIER SCIENCE INC', 'NEW YORK', '655 AVENUE OF THE AMERICAS, NEW YORK, NY 10010', '0034-4257', NULL, 'REMOTE SENS ENVIRON', 'Remote Sens. Environ.', 'APR-JUN', 1989, '28BP 219EP 232', NULL, '10.1016/0034-4257(89)90115-6', 14, 'Environmental Sciences; Remote Sensing; Imaging Science & Photographic   Technology', 'Science Citation Index Expanded (SCI-EXPANDED)', 'Environmental Sciences & Ecology; Remote Sensing; Imaging Science &   Photographic Technology', 'AZ379', 'WOS:A1989AZ37900018', NULL, '2023-11-05', NULL);
