INSERT INTO `artical_data_copy8` VALUES ('J', 'TAKASHIMA, T   MASUDA, K', 'TAKASHIMA, T   MASUDA, K', 'AVERAGED EMISSIVITIES OF QUARTZ AND SAHARA DUST POWDERS IN THE INFRARED   REGION', 'REMOTE SENSING OF ENVIRONMENT', 'English', 'NoteRP TAKASHIMA, T (通讯作者)，METEOROL RES INST, TSUKUBA, IBARAKI 305, JAPAN.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 1, 6, 6, 0, 1, 'ELSEVIER SCIENCE INC', 'NEW YORK', '360 PARK AVE SOUTH, NEW YORK, NY 10010-1710 USA', '0034-4257', NULL, 'REMOTE SENS ENVIRON', 'Remote Sens. Environ.', 'DEC', 1988, '26IS 3BP 301EP 302', NULL, '10.1016/0034-4257(88)90084-3', 2, 'Environmental Sciences; Remote Sensing; Imaging Science & Photographic   Technology', 'Science Citation Index Expanded (SCI-EXPANDED)', 'Environmental Sciences & Ecology; Remote Sensing; Imaging Science &   Photographic Technology', 'T0234', 'WOS:A1988T023400008', NULL, '2023-11-05', NULL);
